    private void txtNamaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtNamaActionPerformed
        // TODO add your handling code here:
        txtEmail.requestFocusInWindow();
        String nama = txtNama.getText();
        System.out.println("Nama : " + nama);
    }//GEN-LAST:event_txtNamaActionPerformed

    private void txtEmailActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtEmailActionPerformed
        // TODO add your handling code here:
        pwPassword.requestFocusInWindow();
        String email = txtEmail.getText();
        System.out.println("Email : " + email);
    }//GEN-LAST:event_txtEmailActionPerformed

    private void pwPasswordActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pwPasswordActionPerformed
        // TODO add your handling code here:
        btnPria.requestFocusInWindow();
        char[] pwchar = pwPassword.getPassword();
        String password = new String(pwchar);
        
        System.out.println("Password : " + password);
    }//GEN-LAST:event_pwPasswordActionPerformed

    private void btnPriaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPriaActionPerformed
        // TODO add your handling code here:
        String JenisKelamin = null;
        if (btnPria.isSelected()) {
            JenisKelamin = "Pria";
        }
        System.out.println("Jenis Kelamin = " + JenisKelamin);
    }//GEN-LAST:event_btnPriaActionPerformed

    private void btnKirimActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnKirimActionPerformed
        // TODO add your handling code here:
        String nama = txtNama.getText();
        System.out.println("Nama : " + nama);
        
        String email = txtEmail.getText();
        System.out.println("Email : " + email);
        
        char[] pwchar = pwPassword.getPassword();
        String password = new String(pwchar);
        System.out.println("Password : " + password);
        
        String JenisKelamin = null;
        if (btnPria.isSelected()) {
            JenisKelamin = "Pria";
        } else {
            JenisKelamin = "Wanita";
        }
        System.out.println("Jenis Kelamin : " + JenisKelamin);
        
        lblInfo.setText("Registrasi Anda Berhasil!");
        
    }//GEN-LAST:event_btnKirimActionPerformed

